import React from 'react';
import '../index.scss';
import Card from '../components/Card';
import { useContext } from 'react';
import AppContext from '../AppContext';

const Drawer = () => {
  const { items, getClick, drawer } = useContext(AppContext);
  return (
    <section class="content">
      <div className="buy">
        <h1>Моя корзина</h1>
        <div className="buyall">Купить все</div>
      </div>

      <div class="cards">
        {drawer.map((get) => {
          const item = items.find((i) => i.id === get.item); // Находим объект
          if (!item) {
            return null; // Или другой способ обработки отсутствующего объекта
          }
          return <Card key={get.id} item={item} getClick={() => getClick(item.id)} />;
        })}
      </div>
    </section>
  );
};

export default Drawer;
