import React from 'react';
import '../index.scss';
import Card from '../components/Card';
import { useContext } from 'react';
import AppContext from '../AppContext';
import axios from 'axios';

const Drawer = () => {
  const { items, getClick, drawer, addToOrders, setOrders, orders } = useContext(AppContext);
  const handleBuyAll = async () => {
    try {
      const itemsToAdd = drawer.map((itemObj) => items.find((item) => item.id === itemObj.item));
      const validItemsToAdd = itemsToAdd.filter((item) => item !== undefined); //Filter out undefined items
      if (validItemsToAdd.length === 0) {
        alert('Корзина пуста или не содержит товаров.');
        return;
      }
      const orderData = validItemsToAdd.map((item) => ({ item: item.id })); // map to order structure.
      const response = await axios.post('http://127.0.0.1:8000/api/orders/', { items: orderData });
      if (response.status === 200) {
        setOrders(response.data.orders); // Crucial: Update the state
      } else {
        const errorData = await response.json();
        throw new Error(
          `Ошибка при добавлении в базу данных: ${errorData.message || response.status}`,
        );
      }
    } catch (error) {
      console.error('Ошибка при добавлении всего:', error);
      alert(`Ошибка при добавлении всего: ${error.message}`);
    }
  };
  return (
    <section className="content">
      <div className="buy">
        <h1>Моя корзина</h1>
        <div className="buyall" onClick={handleBuyAll}>
          Купить все
        </div>
      </div>

      <div className="cards">
        {drawer.map((item) => {
          return (
            <Card
              key={get.id}
              item={item}
              getClick={() => getClick(item.item)}
              noLike={true}
              noPlus={true}
              getPlus={true}
              addToOrders={() => addToOrders(item.item)}
            />
          );
        })}
      </div>
    </section>
  );
};

export default Drawer;
