import React from 'react';
import ContentLoader from 'react-content-loader';

const LoaderCard = () => (
  <ContentLoader
    speed={2}
    width={150}
    height={200}
    viewBox="0 0 150 200"
    backgroundColor="#f0f0f0"
    foregroundColor="#e0e0e0"
  >
    {/* Загрузка изображения */}
    <rect x="0" y="0" rx="10" ry="10" width="150" height="120" />

    {/* Загрузка линии разделителя */}
    <rect x="20" y="125" width="110" height="1" />

    {/* Загрузка названия товара */}
    <rect x="10" y="135" rx="5" ry="5" width="130" height="10" />

    {/* Загрузка цены товара */}
    <rect x="10" y="150" rx="5" ry="5" width="80" height="10" />

    {/* Загрузка иконки лайка */}
    <rect x="130" y="10" width="20" height="20" rx="5" ry="5" />
  </ContentLoader>
);

export default LoaderCard;
