import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../index.scss';
import Card from '../components/Card';

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Функция для получения данных о любимых товарах
    const fetchFavorites = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/likes/');
        setFavorites(response.data);
      } catch (error) {
        console.error('Ошибка при загрузке любимых товаров:', error);
      }
    };

    fetchFavorites();
  }, []);
  return (
    <section className="content">
      <h1>Мои любимые</h1>
      <div className="cards">
        {favorites.map((like) => (
          <Card key={like.id} item={like.item} /> // Предполагается, что like.item содержит данные о товаре
        ))}
      </div>
    </section>
  );
};

export default Favorites;
