import React from 'react';
import '../index.scss';
import Card from '../components/Card';
import { useContext } from 'react';
import AppContext from '../AppContext';

const Drawer = () => {
  const { items, getClick, drawer, addToOrders } = useContext(AppContext);
  const handleBuyAll = () => {
    // Create a new array to hold the IDs, ensuring only valid items are added
    const idsToAdd = drawer.reduce((acc, curr) => {
      const item = items.find((i) => i.id === curr.item);
      if (item) {
        acc.push(item.id);
      }
      return acc;
    }, []);

    // Add the items to the orders state. CRITICAL: Use setOrders.
    setOrders([...orders, ...idsToAdd.map((id) => ({ item: id }))]);
  };
  return (
    <section className="content">
      <div className="buy">
        <h1>Моя корзина</h1>
        <div className="buyall" onClick={handleBuyAll}>
          Купить все
        </div>
      </div>

      <div className="cards">
        {drawer.map((get) => {
          const item = items.find((i) => i.id === get.item); // Находим объект
          if (!item) {
            return null; // Или другой способ обработки отсутствующего объекта
          }
          return (
            <Card
              key={get.id}
              item={item}
              getClick={() => getClick(item.id)}
              noLike={true}
              noPlus={true}
              getPlus={true}
              addToOrders={() => addToOrders(item.id)}
            />
          );
        })}
      </div>
    </section>
  );
};

export default Drawer;
