import '../index.scss';
import Card from '../components/Card';
import React, { useEffect, useContext } from 'react';

import AppContext from '../AppContext';
import LoaderCard from '../components/Loader';
const Home = ({ items, handleLikeClick }) => {
  const { isLoading, setIsLoading } = useContext(AppContext);

  useEffect(() => {
    // Устанавливаем таймер на 1 секунду
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 3000);

    // Очищаем таймер при размонтировании компонента
    return () => clearTimeout(timer);
  }, []);

  return (
    <section className="content">
      <h1>Товары</h1>
      <div className="sort">
        <div className="category">Все</div>
        <div className="category">Кроссовки</div>
        <div className="category">Футболки</div>
        <div className="category">Худи</div>
      </div>

      <div className="cards">
        {isLoading
          ? Array.from({ length: 8 }, (_, index) => <LoaderCard key={index} />)
          : items.map((item) => (
              <Card key={item.id} item={item} onLikeClick={() => handleLikeClick(item.id)} />
            ))}
      </div>
    </section>
  );
};

export default Home;
