import React from 'react';

import '../index.scss';
import Card from '../components/Card';

const Favorites = ({ favorites }) => {
  return (
    <section className="content">
      <h1>Мои любимые</h1>
      <div className="cards">
        {favorites.map((like) => (
          // Используем поля из like.item
          <Card
            key={like.item.id}
            item={{
              id: like.item.id,
              title: like.item.title,
              price: like.item.price,
              image: like.item.image,
              isLiked: true,
            }}
          />
        ))}
      </div>
    </section>
  );
};

export default Favorites;
