import React from 'react';
import ContentLoader from 'react-content-loader';

const LoaderCard = () => (
  <ContentLoader
    speed={2}
    width={150} // Ширина карточки
    height={200} // Высота карточки
    viewBox="0 0 300 400" // Область просмотра
    backgroundColor="#f0f0f0"
    foregroundColor="#e0e0e0"
  >
    {/* Загрузка изображения */}
    <rect x="0" y="0" rx="10" ry="10" width="300" height="200" /> {/* Изображение */}
    {/* Загрузка названия товара */}
    <rect x="10" y="210" rx="5" ry="5" width="280" height="20" /> {/* Название */}
    {/* Загрузка описания товара */}
    <rect x="10" y="240" rx="5" ry="5" width="250" height="15" /> {/* Описание */}
    {/* Загрузка цены товара */}
    <rect x="10" y="270" rx="5" ry="5" width="100" height="20" /> {/* Цена */}
    {/* Загрузка иконки лайка */}
    <rect x="260" y="10" width="30" height="30" rx="5" ry="5" /> {/* Иконка лайка */}
  </ContentLoader>
);

export default LoaderCard;
