import React from 'react';

import '../index.scss';
import Card from '../components/Card';

const Favorites = ({ favorites, onLikeClick }) => {
  return (
    <section className="content">
      <h1>Мои любимые</h1>
      <div className="cards">
        {favorites.map((like) => (
          <Card
            key={like.id}
            item={{
              id: item.id,
              title: item.title,
              price: item.price,
              image: item.image,
              isLiked: true, // Поскольку это фавориты, можно установить isLiked в true
            }}
          /> // Предполагается, что like.item содержит данные о товаре
        ))}
      </div>
    </section>
  );
};

export default Favorites;
