import '../index.scss';
import Card from '../components/Card';
import React from 'react';
import ContentLoader from 'react-content-loader';

const Home = ({ items, handleLikeClick, isLoading }) => {
  return (
    <section className="content">
      <h1>Товары</h1>
      <div className="sort">
        <div className="category">Все</div>
        <div className="category">Кроссовки</div>
        <div className="category">Футболки</div>
        <div className="category">Худи</div>
      </div>

      <div className="cards">
        {isLoading ? (
          <ContentLoader
            speed={2}
            width={150}
            height={200}
            viewBox="0 0 150 200"
            backgroundColor="#e0e0e0"
            foregroundColor="#f0f0f0"
          >
            <circle cx="20" cy="20" r="20" /> {/* Иконка лайка */}
            <rect x="0" y="50" rx="10" ry="10" width="150" height="100" />{' '}
            {/* Изображение карточки */}
            <rect x="10" y="160" rx="5" ry="5" width="70" height="15" /> {/* Название */}
            <rect x="90" y="160" rx="5" ry="5" width="50" height="15" /> {/* Цена */}
            <circle cx="120" cy="180" r="15" /> {/* Кнопка плюс */}
          </ContentLoader>
        ) : (
          items.map((item) => (
            <Card key={item.id} item={item} onLikeClick={() => handleLikeClick(item.id)} />
          ))
        )}
      </div>
    </section>
  );
};

export default Home;
