import './index.scss';
import Header from './components/Header';
import Home from './pages/Home';
import Favorites from './pages/Favorites';
import Drawer from './pages/Drawer';
import Order from './pages/Order';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [items, setItems] = useState([]);
  const [favorites, setFavorites] = useState([]);
  useEffect(() => {
    // Получаем данные из Django API
    const fetchData = async () => {
      try {
        const [itemsResponse, likeResponse] = await Promise.all([
          axios.get('http://127.0.0.1:8000/api/items/'),
          axios.get('http://127.0.0.1:8000/api/likes/'),
        ]);
        setItems(itemsResponse.data);
        setFavorites(likeResponse.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  const handleLikeClick = async (itemId) => {
    try {
      // Проверяем, есть ли лайк
      const isLiked = items.some((item) => item.id === itemId && item.isLiked);

      if (isLiked) {
        // Удалить лайк
        await axios.delete(`http://127.0.0.1:8000/api/likes/${itemId}/`);
      } else {
        // Добавить лайк
        await axios.post(`http://127.0.0.1:8000/api/likes/`, {
          item: itemId,
        });
      }
      // Обновляем состояние лайка
      setItems((prevItems) =>
        prevItems.map((item) => {
          if (item.id === itemId) {
            return { ...item, isLiked: !item.isLiked };
          } else {
            return item;
          }
        }),
      );
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <BrowserRouter>
      <div className="container">
        <Header />
        <Routes>
          <Route path="/" element={<Home items={items} handleLikeClick={handleLikeClick} />} />
          <Route path="/Favorites" element={<Favorites favorites={favorites} />} />
          <Route path="/Drawer" element={<Drawer />} />
          <Route path="/Order" element={<Order />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
