import React from 'react';
import '../index.scss';
import Card from '../components/Card';
import { useContext } from 'react';
import AppContext from '../AppContext';

const Drawer = () => {
  const { items, getClick, drawer, addToOrders, removeFromDrawer } = useContext(AppContext);

  const addAllToOrders = async () => {
    try {
      // Проходим по всем элементам в drawer
      for (const item of drawer) {
        await addToOrders(item.item); // Вызываем addToOrders для каждого элемента

        removeFromDrawer(item.id);
      }
    } catch (error) {
      console.error('Ошибка при добавлении всех товаров в заказы:', error);
    }
  };

  return (
    <section className="content">
      <div className="buy">
        <h1>Моя корзина</h1>
        <div className="buyall" onClick={addAllToOrders}>
          Купить все
        </div>
      </div>

      <div className="cards">
        {drawer.map((get) => {
          const item = items.find((i) => i.id === get.item); // Находим объект
          if (!item) {
            return null; // Или другой способ обработки отсутствующего объекта
          }
          return (
            <Card
              key={get.id}
              item={item}
              getClick={() => getClick(item.id)}
              noLike={true}
              noPlus={true}
              getPlus={true}
              addToOrders={() => {
                addToOrders(item.id);
                removeFromDrawer(get.id);
              }}
            />
          );
        })}
      </div>
    </section>
  );
};

export default Drawer;
