import './index.scss';
import Header from './components/Header';
import Home from './pages/Home';
import Favorites from './pages/Favorites';
import Drawer from './pages/Drawer';
import Order from './pages/Order';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [items, setItems] = useState([]);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Получаем данные из Django API
    const fetchData = async () => {
      try {
        const [itemsResponse, likeResponse] = await Promise.all([
          axios.get('http://127.0.0.1:8000/api/items/'),
          axios.get('http://127.0.0.1:8000/api/likes/'),
        ]);
        const itemsData = itemsResponse.data.map((item) => {
          const isLiked = likesResponse.data.some((like) => like.item === item.id);
          return { ...item, isLiked }; // Добавляем isLiked к каждому объекту
        });
        setItems(itemsData);
        setFavorites(likeResponse.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  const handleLikeClick = async (itemId) => {
    try {
      const isLiked = favorites.some((like) => like.item === itemId);

      if (isLiked) {
        // Находим объект лайка, чтобы получить его ID
        const likeToRemove = favorites.find((like) => like.item === itemId);

        if (!likeToRemove) {
          console.error('Лайк не найден в массиве favorites. Это неожиданно.');
          return; // Или выбросить ошибку
        }

        // Используем likeToRemove.id для удаления
        await axios.delete(`http://127.0.0.1:8000/api/likes/${likeToRemove.id}/`);

        // Обновляем состояние favorites (теперь корректно фильтруем по ID лайка)
        setFavorites((prevFavorites) =>
          prevFavorites.filter((like) => like.id !== likeToRemove.id),
        );

        // Обновляем состояние items (без изменений, но упрощено)
        setItems((prevItems) =>
          prevItems.map((item) => (item.id === itemId ? { ...item, isLiked: false } : item)),
        );
      } else {
        const response = await axios.post('http://127.0.0.1:8000/api/likes/', { item: itemId });

        // Используем данные ответа (которые включают ID лайка)
        setFavorites((prevFavorites) => [...prevFavorites, response.data]);

        setItems((prevItems) =>
          prevItems.map((item) => (item.id === itemId ? { ...item, isLiked: true } : item)),
        );
      }
    } catch (error) {
      console.error('Ошибка обработки лайка:', error); // Более информативное сообщение об ошибке
    }
  };
  return (
    <BrowserRouter>
      <div className="container">
        <Header />
        <Routes>
          <Route path="/" element={<Home items={items} handleLikeClick={handleLikeClick} />} />
          <Route path="/Favorites" element={<Favorites favorites={favorites} />} />
          <Route path="/Drawer" element={<Drawer />} />
          <Route path="/Order" element={<Order />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
