import React from 'react';
import '../index.scss';
import Card from '../components/Card';
import { useContext } from 'react';
import AppContext from '../AppContext';

const Favorites = () => {
  const { favorites, items, handleLikeClick } = useContext(AppContext); // Получаем favorites и items из контекста

  return (
    <section className="content">
      <h1>Мои любимые</h1>
      <div className="cards">
        {favorites.map((like) => {
          const item = items.find((i) => i.id === like.item); // Находим объект
          if (!item) {
            return null; // Или другой способ обработки отсутствующего объекта
          }
          return <Card key={like.id} item={item} onLikeClick={() => handleLikeClick(item.id)} />; // Передаем найденный объект в Card
        })}
      </div>
    </section>
  );
};

export default Favorites;
