import '../index.scss';
import Card from '../components/Card';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Home = () => {
  const [items, setItems] = useState([]);
  const [userId, setUserId] = useState(1);

  useEffect(() => {
    // Получаем данные из Django API
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/items/'); // Замените на ваш API-адрес
        setItems(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  const handleLikeClick = async (itemId) => {
    try {
      // Проверяем, есть ли лайк
      const isLiked = items.some((item) => item.id === itemId && item.isLiked);

      if (isLiked) {
        // Удалить лайк
        await axios.delete(`http://127.0.0.1:8000/api/likes/${userId}/${itemId}`);
        await handleDelete(itemId);
      } else {
        // Добавить лайк
        await axios.post(`http://127.0.0.1:8000/api/likes/`, {
          user_id: userId,
          item: itemId,
        });
      }
      // Обновляем состояние лайка
      setItems((prevItems) =>
        prevItems.map((item) => {
          if (item.id === itemId) {
            return { ...item, isLiked: !item.isLiked };
          } else {
            return item;
          }
        }),
      );
    } catch (error) {
      console.error(error);
    }
  };
  const handleDelete = async (itemId) => {
    try {
      // Удаление объекта из базы данных
      await axios.delete(`http://127.0.0.1:8000/api/items/${itemId}/`);
      // Удаляем объект из состояния
      setItems((prevItems) => prevItems.filter((item) => item.id !== itemId));
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <section className="content">
      <h1>Товары</h1>
      <div className="sort">
        <div className="category">Все</div>
        <div className="category">Кроссовки</div>
        <div className="category">Футболки</div>
        <div className="category">Худи</div>
      </div>
      <div className="cards">
        {items.map((item) => (
          <Card key={item.id} item={item} onLikeClick={() => handleLikeClick(item.id)} />
        ))}
      </div>
    </section>
  );
};

export default Home;
