import './index.scss';
import Header from './components/Header';
import Home from './pages/Home';
import Favorites from './pages/Favorites';
import Drawer from './pages/Drawer';
import Order from './pages/Order';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [items, setItems] = useState([]);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Получаем данные из Django API
    const fetchData = async () => {
      try {
        const [itemsResponse, likeResponse] = await Promise.all([
          axios.get('http://127.0.0.1:8000/api/items/'),
          axios.get('http://127.0.0.1:8000/api/likes/'),
        ]);
        setItems(itemsResponse.data);
        setFavorites(likeResponse.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  const handleLikeClick = async (itemId) => {
    try {
      // Проверяем, есть ли лайк
      const isLiked = favorites.some((like) => like.item === itemId);

      if (isLiked) {
        // Удаляем лайк из базы данных
        await axios.delete(`http://127.0.0.1:8000/api/likes/${itemId}/`); // Исправленный URL

        // Обновляем состояние favorites
        setFavorites((prevFavorites) => prevFavorites.filter((like) => like.item !== itemId));

        // Обновляем состояние items
        setItems((prevItems) =>
          prevItems.map((item) => {
            if (item.id === itemId) {
              return { ...item, isLiked: false };
            } else {
              return item;
            }
          }),
        );
      } else {
        // Добавляем лайк в базу данных
        await axios.post('http://127.0.0.1:8000/api/likes/', {
          item: itemId,
        });

        // Обновляем состояние favorites
        setFavorites((prevFavorites) => [...prevFavorites, { item: itemId }]);

        // Обновляем состояние items
        setItems((prevItems) =>
          prevItems.map((item) => {
            if (item.id === itemId) {
              return { ...item, isLiked: true };
            } else {
              return item;
            }
          }),
        );
      }
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <BrowserRouter>
      <div className="container">
        <Header />
        <Routes>
          <Route path="/" element={<Home items={items} handleLikeClick={handleLikeClick} />} />
          <Route path="/Favorites" element={<Favorites favorites={favorites} />} />
          <Route path="/Drawer" element={<Drawer />} />
          <Route path="/Order" element={<Order />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
