import '../index.scss';
import Card from '../components/Card';
import React, { useContext, useState, useEffect } from 'react';
import LoaderCard from '../components/Loader';
import AppContext from '../AppContext';

const Home = ({ items }) => {
  const { isLoading, likeClick, getClick } = useContext(AppContext);
  const [filteredItems, setFilteredItems] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    if (!isLoading) {
      setFilteredItems(items);
    }
  }, [items, isLoading, setFilteredItems]);

  const handleCategoryClick = (category) => {
    const filtered =
      category === 'Все' ? items : items.filter((item) => item.category === category);
    setFilteredItems(filtered);
  };

  const handleSearch = (e) => {
    const query = e.target.value.toLowerCase();
    setSearchQuery(query);
    const filtered = items.filter((item) => {
      return (
        item.name.toLowerCase().includes(query) ||
        item.description.toLowerCase().includes(query) ||
        item.category.toLowerCase().includes(query)
      );
    });
    setFilteredItems(filtered);
  };

  return (
    <section className="content">
      <h1>Товары</h1>
      <div className="sort">
        <div className="categs">
          <div className="category" onClick={() => handleCategoryClick('Все')}>
            Все
          </div>
          <div className="category" onClick={() => handleCategoryClick('Кроссовки')}>
            Кроссовки
          </div>
          <div className="category" onClick={() => handleCategoryClick('Футболки')}>
            Футболки
          </div>
          <div className="category" onClick={() => handleCategoryClick('Кофта')}>
            Кофта
          </div>
        </div>
        <input
          type="text"
          placeholder="Введите ваш запрос..."
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>

      <div className="cards">
        {isLoading
          ? Array.from({ length: 10 }, (_, index) => (
              <div className="loader-card-container" key={index}>
                <LoaderCard />
              </div>
            ))
          : items.map((item) => (
              <Card key={item.id} item={item} onLikeClick={likeClick} getClick={getClick} />
            ))}
      </div>
    </section>
  );
};

export default Home;
