import '../index.scss';
import Card from '../components/Card';
import React, { useEffect, useContext } from 'react';
import ContentLoader from 'react-content-loader';
import AppContext from '../AppContext';

const Home = ({ items, handleLikeClick, isLoading }) => {
  const { setIsLoading } = useContext(AppContext);
  useEffect(() => {
    // Устанавливаем таймер на 1 секунду
    const timer = setTimeout(() => {
      setIsLoading(false); // После 1 секунды меняем состояние на false
    }, 1000);

    // Очищаем таймер при размонтировании компонента
    return () => clearTimeout(timer);
  }, []);
  return (
    <section className="content">
      <h1>Товары</h1>
      <div className="sort">
        <div className="category">Все</div>
        <div className="category">Кроссовки</div>
        <div className="category">Футболки</div>
        <div className="category">Худи</div>
      </div>

      <div className="cards">
        {isLoading
          ? Array.from({ length: 8 }, (_, index) => (
              <ContentLoader
                speed={2}
                width={150}
                height={200}
                viewBox="0 0 150 200"
                backgroundColor="#f0f0f0"
                foregroundColor="#e0e0e0"
              >
                <rect x="0" y="0" rx="10" ry="10" width="150" height="120" /> {/* Изображение */}
                <rect x="10" y="130" rx="5" ry="5" width="130" height="10" /> {/* Название */}
                <rect x="10" y="150" rx="5" ry="5" width="80" height="10" /> {/* Цена */}
              </ContentLoader>
            ))
          : items.map((item) => (
              <Card key={item.id} item={item} onLikeClick={() => handleLikeClick(item.id)} />
            ))}
      </div>
    </section>
  );
};

export default Home;
