import './index.scss';
import Header from './components/Header';
import Home from './pages/Home';
import Favorites from './pages/Favorites';
import Drawer from './pages/Drawer';
import Order from './pages/Order';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [items, setItems] = useState([]);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Получаем данные из Django API
    const fetchData = async () => {
      try {
        const [itemsResponse, likeResponse] = await Promise.all([
          axios.get('http://127.0.0.1:8000/api/items/'),
          axios.get('http://127.0.0.1:8000/api/likes/'),
        ]);
        setItems(itemsResponse.data);
        setFavorites(likeResponse.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  const handleLikeClick = async (itemId) => {
    try {
      // Проверяем, есть ли лайк
      const isLiked = favorites.some((like) => like.item.id === itemId);
      if (isLiked) {
        console.log('Этот элемент уже добавлен в избранное.');
        return;
      } else {
        // Находим элемент по itemId
        const itemToLike = items.find((item) => item.id === itemId);
        if (!itemToLike) {
          console.error('Элемент не найден');
          return;
        }

        // Создаем объект item с необходимыми полями
        const itemData = {
          image: itemToLike.image, // Предполагается, что у вас есть это поле
          title: itemToLike.title,
          price: itemToLike.price,
        };

        // Добавляем лайк
        await axios.post('http://127.0.0.1:8000/api/likes/', {
          item: itemData, // Отправляем объект с необходимыми полями
        });

        // Обновляем состояние лайка
        setFavorites((prevFavorites) => [
          ...prevFavorites,
          { id: Date.now(), item: { id: itemId } },
        ]);

        setItems((prevItems) =>
          prevItems.map((item) => {
            if (item.id === itemId) {
              return { ...item, isLiked: true };
            } else {
              return item;
            }
          }),
        );
      }
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <BrowserRouter>
      <div className="container">
        <Header />
        <Routes>
          <Route path="/" element={<Home items={items} handleLikeClick={handleLikeClick} />} />
          <Route path="/Favorites" element={<Favorites favorites={favorites} />} />
          <Route path="/Drawer" element={<Drawer />} />
          <Route path="/Order" element={<Order />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
