import React from 'react';
import './Card.scss';
import LoaderCard from '../components/Loader';

function Card({ item, onLikeClick, isLoading }) {
  return (
    <div className="card item">
      {isLoading ? (
        <div className="loader-card">
          <LoaderCard /> {/* Здесь можно добавить индикатор загрузки для каждой карточки */}
        </div>
      ) : (
        <>
          <img
            className="like"
            src={item.isLiked ? '/img/like2.png' : '/img/like.png'}
            alt=""
            onClick={() => onLikeClick(item.id)}
          />
          <img src={item.image} alt={item.title} />
          <div className="cardofitems">
            <div className="card-name">
              <p className="name-line">{item.title}</p>
              <p className="price-line">Цена: {item.price} p.</p>
            </div>
            <img src="/img/plus.png" alt="" />
          </div>
        </>
      )}
    </div>
  );
}
export default Card;
