import '../index.scss';
import Card from '../components/Card';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Home = ({ items, handleLikeClick }) => {
  return (
    <section className="content">
      <h1>Товары</h1>
      <div className="sort">
        <div className="category">Все</div>
        <div className="category">Кроссовки</div>
        <div className="category">Футболки</div>
        <div className="category">Худи</div>
      </div>
      <div className="cards">
        {items.map((item) => (
          <Card key={item.id} item={item} onLikeClick={() => handleLikeClick(item.id)} />
        ))}
      </div>
    </section>
  );
};

export default Home;
