import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../index.scss';
import Card from '../components/Card';

const Favorites = ({ favorites }) => {
  return (
    <section className="content">
      <h1>Мои любимые</h1>
      <div className="cards">
        {favorites.map((like) => (
          <Card key={like.id} item={like.item} /> // Предполагается, что like.item содержит данные о товаре
        ))}
      </div>
    </section>
  );
};

export default Favorites;
